function a(e){return new Intl.NumberFormat("pt-BR",{style:"currency",currency:"BRL"}).format(e)}function o(e){return new Intl.NumberFormat("pt-BR").format(e)}function c(e){return`${e.toFixed(1).replace(".",",")}%`}function u(e){const t=new Date(e);return new Intl.DateTimeFormat("pt-BR",{day:"2-digit",month:"2-digit",year:"numeric"}).format(t)}function i(e){const t=e.replace(/\D/g,"");return t.length===11?`(${t.slice(0,2)}) ${t.slice(2,7)}-${t.slice(7)}`:t.length===10?`(${t.slice(0,2)}) ${t.slice(2,6)}-${t.slice(6)}`:e}function s(e){const[t,r]=e.split("-");return`${["Jan","Fev","Mar","Abr","Mai","Jun","Jul","Ago","Set","Out","Nov","Dez"][parseInt(r)-1]}/${t}`}export{a,u as b,i as c,c as d,s as e,o as f};
